export default (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y){return {data:{"/article/sak1/eleison-til-menighetslederne":{_path:h,_dir:"sak1",_draft:f,_partial:f,_locale:n,_empty:f,title:g,description:o,series:e,showTime:f,showOutdatedWarning:i,created:p,updated:p,tags:[e],body:{type:"root",children:[{type:a,tag:"h1",props:{id:"eleison-til-menighetslederne"},children:[{type:b,value:g}]},{type:a,tag:c,props:{},children:[{type:b,value:"Viser til brevet fra "},{type:a,tag:q,props:{href:j},children:[{type:b,value:k}]},{type:b,value:".\r\nBibelen oppfordrer de kristne til at de "},{type:a,tag:"strong",props:{},children:[{type:b,value:"ikke"}]},{type:b,value:" skal overlate meglingen mellom seg innbyrdes til utenforstående kontrollorganer som er styrt av urettferdige mennesker. 1 Kor 6, 1."}]},{type:a,tag:c,props:{},children:[{type:b,value:"Saken er at undertegnede er blitt kjent med at det er en rik mann i menigheten som er under beskylding for å forsøke å gjøre seg enda rikere ved urettmessig å tilrøve seg verdier fra en arv som andre fattige søsken, har langt større behov for (og rett til) enn han."}]},{type:a,tag:c,props:{},children:[{type:b,value:"Spørsmålet er om dere som styrer menigheten er interessert i å megle i konflikten eller om dere velsigner eller anbefaler partene å bruke verdslige domstoler (Forliksråd og deretter domstol)."}]},{type:a,tag:c,props:{},children:[{type:b,value:"Hvis undertegnede ikke hører noe fra dere, må det regnes som at \"den som tier, samtykker\"."}]},{type:a,tag:c,props:{},children:[{type:b,value:"Undertegnedes avsluttende tanke er at de fleste av de som styrer menigheter selv kan regnes som rike, og at det da burde falle mest naturlig for dem å ha mer sympati med sine rike menighetsfeller, enn de fattige og utstøtte. Det er opp til dere å motbevise at dette ikke er tilfellet i denne menigheten."}]},{type:a,tag:r,props:{open:i,dataHeadingId:l},children:[{type:a,tag:s,props:{},children:[{type:a,tag:t,props:{id:l,dataHeadingPath:"{\"h2\":\"forsøk-på-forlik\"}"},children:[{type:b,value:u}]}]},{type:a,tag:c,props:{},children:[{type:b,value:"Den rike mann har vært i kontakt med sine søsken om saken og ikke vært villig til å innrømme at gjeldsbrevet hans er uredelig eller at han har forsøkt å svike sine søsken, til fordel for sitt eget hus."}]}]},{type:a,tag:r,props:{open:i,dataHeadingId:m},children:[{type:a,tag:s,props:{},children:[{type:a,tag:t,props:{id:m,dataHeadingPath:"{\"h2\":\"to-eller-tre-vitner\"}"},children:[{type:b,value:v}]}]},{type:a,tag:c,props:{},children:[{type:b,value:"Det betyr at anbefalingen etter Matt 18, 15-17 da er å ta saken opp med to eller tre vitner. Det er tre som står oppført som styremedlemmer i den "},{type:a,tag:q,props:{href:"https:\u002F\u002Fw2.brreg.no\u002Fenhet\u002Fsok\u002Fdetalj.jsp?orgnr=994009087",":target":"_blank",rel:["nofollow"]},children:[{type:b,value:"Luthersk læstadianske forsamling i Tromsø (lyngenretningen)"}]}]},{type:a,tag:c,props:{},children:[{type:b,value:"Ta kontakt med undertegnede (på epost) for å få telefonnumrene til partene. Kun seriøse henvendelser godtas. All respons offentliggjøres."}]},{type:a,tag:c,props:{},children:[{type:b,value:"Vennlig hilsen"}]},{type:a,tag:c,props:{},children:[{type:b,value:"Eleison\r\nJurist"}]}]}],toc:{title:n,searchDepth:w,depth:w,links:[{id:l,depth:x,text:u},{id:m,depth:x,text:v}]}},_type:d,_id:y,_source:"content",_file:"1.article\u002F1.sak1\u002Feleison-til-menighetslederne.md",_extension:"md"},"ugyldighet-series":[{_path:j,title:k,description:"Kjære rike mann!\nViser til Din beslutning om salg av arvet leilighet i det åpne eiendomsmarkedet (til tredjeperson).\nDet er flere andre arveberettigede og din beslutning er tatt uten samtykke eller samtaler med de andre arveberettigede, om saken.",_type:d},{_path:h,title:g,description:o,_type:d}],rootFolder:[{title:"Article",_path:"\u002Farticle",children:[{title:"Sak1",_path:"\u002Farticle\u002Fsak1",children:[{title:k,_path:j,_id:"content:1.article:1.sak1:eleison-til-den-rike-mann.md",_type:d,series:e,tags:[e]},{title:g,_path:h,_id:y,_type:d,series:e,tags:[e]}]},{title:"Forord",_path:"\u002Farticle\u002Fforord",_id:"content:1.article:2.forord.md",_type:d,tags:["forord"]}]},{title:"Om Eleison",_path:"\u002Fabout",_id:"content:2.about.md",_type:d},{title:"Eleison",_path:"\u002F",_id:"content:3.index.md",_type:d}]},prerenderedAt:void 0}}("element","text","p","markdown","ugyldighet",false,"Eleison til menighetslederne","\u002Farticle\u002Fsak1\u002Feleison-til-menighetslederne",true,"\u002Farticle\u002Fsak1\u002Feleison-til-den-rike-mann","Eleison til den rike mann","forsøk-på-forlik","to-eller-tre-vitner","","Viser til brevet fra Eleison til den rike mann.\r\nBibelen oppfordrer de kristne til at de ikke skal overlate meglingen mellom seg innbyrdes til utenforstående kontrollorganer som er styrt av urettferdige mennesker. 1 Kor 6, 1.","2023-05-09T00:00:00.000Z","a","details","summary","h2","Forsøk på forlik","To eller tre vitner",5,2,"content:1.article:1.sak1:eleison-til-menighetslederne.md"))